/* eslint-disable import/no-extraneous-dependencies */
import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import './search-item-list.css';
import SwapiService from '../../services/swapi-service';
import SearchList from '../search-list';
import PaginationSearch from '../pagination-search';
import Spinner from '../spinner';

export default function SearchItemList() {
  const { str } = useParams();
  const swapiService = new SwapiService();
  const [term, setTerm] = useState(null);
  const [list, setList] = useState(null);
  const [count, setCount] = useState(null);
  const [currentElement, setCurrentElement] = useState(1);
  const [display, setDisplay] = useState(true);
  const history = useHistory();
  let searchItem;
  const onHandleSubmit = (event) => {
    swapiService.getElement(searchItem).then((listFromServer) => {
      setCount(listFromServer.length);
      setList(listFromServer);
    });
    if (event !== undefined) {
      swapiService.getElement(term).then((listFromServer) => {
        setCount(listFromServer.length);
        setList(listFromServer);
        history.push(`/search=${term}`);
      });
      event.preventDefault();
    }
  };
  const onTermChange = (e) => {
    if (typeof e === 'string') {
      searchItem = e;
      setTerm(searchItem);
      onHandleSubmit();
    }
    if (e.target !== undefined) {
      searchItem = e.target.value;
      setTerm(e.target.value);
      if (e.target.value !== '') {
        setDisplay(true);
        onHandleSubmit();
      } else setDisplay(false);
    }
  };
  useEffect(() => {
    onTermChange(str);
  }, []);

  if (!list) return <Spinner />;

  return (
    <div>
      <form onSubmit={onHandleSubmit}>
        <input type="text" value={term} onInput={onTermChange} />
        <input type="submit" value="go" />
      </form>
      {list && display && (
        <SearchList
          list={list}
          propTerm={term}
          display={display}
          currentPage={currentElement}
        />
      )}
      {list && display && (
        <PaginationSearch
          totalCount={count}
          currentPage={currentElement}
          setCurrentPage={setCurrentElement}
          name={list}
        />
      )}
    </div>
  );
}
