/* eslint-disable import/no-extraneous-dependencies */
import React, { useState, useEffect } from 'react';
import { useLocation, useHistory } from 'react-router-dom';
import quetyString from 'query-string';
import './search-item-list.css';
import SwapiService from '../../services/swapi-service';
import SearchList from '../search-list';
import PaginationSearch from '../pagination-search';
import Spinner from '../spinner';

export default function SearchItemList() {
  const location = useLocation();
  const swapiService = new SwapiService();
  const [term, setTerm] = useState(null);
  const [list, setList] = useState(null);
  const [count, setCount] = useState(null);
  const [currentElement, setCurrentElement] = useState(1);
  const [display, setDisplay] = useState(true);
  const history = useHistory();
  const parsedQuery = quetyString.parse(location.search);

  const onHandleSubmit = (event) => {
    // setTimeout(() => {
    //   swapiService
    //     .getElement(term)
    //     .then(([listFromServer, countFromServer]) => {
    //       setCount(countFromServer);
    //       setList(listFromServer);
    //     });
    // }, 2000);
    if (event !== undefined) {
      setTimeout(() => {
        swapiService
          .getElement(parsedQuery.page, term)
          .then(([listFromServer, countFromServer]) => {
            setCount(countFromServer);
            setList(listFromServer);
            history.push(`/search=${term}`);
          });
      }, 2000);
      event.preventDefault();
    }
  };
  const onTermChange = (e) => {
    let searchItem;
    if (typeof e === 'string') {
      console.log(e);
      searchItem = e;
      setTerm(searchItem);
      onHandleSubmit();
    }
    if (e.target !== undefined) {
      searchItem = e.target.value;
      setTerm(e.target.value);
      if (e.target.value !== '') {
        setDisplay(true);
